INSERT INTO arabic_dictionaries VALUES ('1','إِمَامٌ','ইমাম','imam',NULL,'-1','1','2021-09-22 03:48:37','2021-09-22 03:48:37'),('2','وَلَدٌ','ছেলে','Boy',NULL,'-1','1','2021-09-22 03:48:37','2021-09-22 03:48:37'),('4','مَا','কী, কী জিনিস','What ?, that which','প্রশ্নবোধক',NULL,'1','2021-09-26 06:24:13','2021-09-26 06:24:13'),('5','مَن','কে, কারা, কাকে, কার ?','Who ?, the one who','প্রশ্নবোধক',NULL,'1','2021-09-26 06:24:13','2021-09-26 06:24:13'),('6','مَتَى','কখন, কবে ?','when ?, the time when','প্রশ্নবোধক',NULL,'1','2021-09-26 06:24:13','2021-09-26 06:24:13'),('7','أَيْنَ','কোথায়, কোন স্থানে ?','Where','প্রশ্নবোধক',NULL,'1','2021-09-26 06:24:13','2021-09-26 06:24:13'),('8','كَيْفَ','কেমন করে, কিভাবে, কিরূপে ?','How many','প্রশ্নবোধক',NULL,'1','2021-09-26 06:24:13','2021-09-26 06:24:13'),('9','شَدِيْدٌ','কড়া, কঠোর, শক্তিশালী','Severe, strong','গুণবাচক শব্দ',NULL,'1','2021-09-26 06:46:43','2021-09-26 06:46:43'),('10','عَلِىّ','সর্বোচ্চ, মহত্তম সত্ত্বা আল্লাহ্‌','High, exalted','গুণবাচক শব্দ',NULL,'1','2021-09-26 06:46:43','2021-09-26 06:46:43'),('11','عَلِيْمٌ','মহাজ্ঞ্যানীঃ  আল্লাহ্‌','Knower','গুণবাচক শব্দ',NULL,'1','2021-09-26 06:46:43','2021-09-26 06:46:43'),('12','قَرِيْبٌ','নিকটবর্তী সত্ত্বাঃ আল্লাহ্‌','Near','গুণবাচক শব্দ','0','1','2021-09-26 06:46:43','2021-09-26 06:48:25'),('13','كَبِيْرٌ','মহামহিমঃ আল্লাহ্‌','Big','গুণবাচক শব্দ','0','1','2021-09-26 06:46:43','2021-09-26 06:47:58'),('14','كَثِيْرٌ','প্রচুর, অঢেল, অগণীত','Plenty, much','গুণবাচক শব্দ','0','1','2021-09-26 06:46:43','2021-09-26 16:47:11'),('15','إِسْمٌ','নাম',NULL,NULL,NULL,'1','2021-09-26 06:50:34','2021-09-26 06:50:34'),('16','فَوْقَ','উপরে','Above, up','কোথায় ?',NULL,'1','2021-09-27 06:37:40','2021-09-27 06:37:40'),('17','تَحْتَ','নিচে, নিম্নে, ভিতরে','Under','কোথায় ?',NULL,'1','2021-09-27 06:37:40','2021-09-27 06:37:40'),('18','بَيْنَ اَيْدِىْ','সামনে','In front of','কোথায় ?',NULL,'1','2021-09-27 06:37:40','2021-09-27 06:37:40'),('19','خَلْفَ','পিছনে','back, After','কোথায় ?',NULL,'1','2021-0